### Orders 4 to 7:

polynom.cl2.gen = function(s, n=length(s)) {
	# - precomputed formulas for the coefficients;
	# - s = shifts of the root-components;
	# - DOES include s0 (as it interacts with all terms in x^(n-1)),
	#   BUT the number of monoms increased hugely!
	if(length(s) < n + 1) s = c(s, rep(0, n - length(s) + 1))
	
	### Order 4:
	if(n == 4) {
		coeff = coeff.cl2.order4.f(s);
	}
	
	### Order 5:
	else if(n == 5) {
		coeff = coeff.cl2.order5.f(s);
	}
	
	### Order 6:
	else if(n == 6) {
		coeff = coeff.cl2.order6.f(s)
	}
	
	### Order 7:
	else if(n == 7) {
		# too long;
		coeff = coeff.cl2.order7.f(s);
	}
	#
	class(coeff) = c(class(coeff), "polynomial")
	coeff = change.origin(coeff, - s[1]);
	### Roots:
	# all n roots: computed exactly (with root of unity of order (n + 1))!
	### TODO:
	# r = roots.cl2.f(s)
	m = unity(n+1, all=T)[-1];
	r = sapply(seq(n), function(id) sum(s * m[id]^(0:n)))
	r = round0(r)
	#
	return(list(p=coeff, x=r))
}
coeff.cl2.order4.f = function(s) {
	s0 = s[1]; s1 = s[2]; s2 = s[3]; s3 = s[4]; s4 = s[5];
	# without s0: only 70 monoms;
	c((- s1*s2*s3*s4 + 2*s1*s2*s3^2 - 3*s1*s2*s4^2 - 3*s1*s2^2*s3 + 2*s1*s2^2*s4 - s1*s2^3 +
		2*s1*s3*s4^2 + 2*s1*s3^2*s4 - s1*s3^3 - s1*s4^3 + 2*s1^2*s2*s3 + 2*s1^2*s2*s4 + s1^2*s2^2 +
		- 3*s1^2*s3*s4 + s1^2*s3^2 + s1^2*s4^2 - s1^3*s2 - s1^3*s3 - s1^3*s4 + s1^4 + 2*s2*s3*s4^2 +
		- 3*s2*s3^2*s4 - s2*s3^3 - s2*s4^3 + 2*s2^2*s3*s4 + s2^2*s3^2 + s2^2*s4^2 - s2^3*s3 - s2^3*s4 +
		s2^4 - s3*s4^3 + s3^2*s4^2 - s3^3*s4 + s3^4 + s4^4),
	(s1*s2*s3 + s1*s2*s4 - 2*s1*s2^2 + s1*s3*s4 + 3*s1*s3^2 - 2*s1*s4^2 + 3*s1^2*s2 - 2*s1^2*s3 +
		- 2*s1^2*s4 + s1^3 + s2*s3*s4 - 2*s2*s3^2 - 2*s2*s4^2 - 2*s2^2*s3 + 3*s2^2*s4 + s2^3 +
		3*s3*s4^2 - 2*s3^2*s4 + s3^3 + s4^3),
	(2*s1*s2 + 2*s1*s3 - 3*s1*s4 + s1^2 - 3*s2*s3 + 2*s2*s4 + s2^2 + 2*s3*s4 + s3^2 + s4^2),
	(s1 + s2 + s3 + s4), 1)
}
